mapper.select:
 select rownum as support_indexnum, sup_apply_code, sup_code, mem_code, to_char(sup_counseling_date, 'YYYY-MM-DD') as sup_counseling_date, sup_content, sup_email, to_char(sup_apply_date, 'YYYY-MM-DD') as sup_apply_date, sup_name
	from (select sp.sup_apply_code, sp.sup_code, sp.mem_code, sp.sup_counseling_date, sp.sup_content, sp.sup_email, sp.sup_apply_date, s.sup_name 
	from support_apply sp, support s where sp.sup_code = s.sup_code order by sp.sup_apply_code) where mem_code=#{mem_code}

 select rownum as partner_indexnum, ptr_apply_code, ptr_code, mem_code, to_char(ptr_apply_date, 'YYYY-MM-DD') as ptr_apply_date, to_char(ptr_start, 'YYYY-MM-DD') as ptr_start, to_char(ptr_end, 'YYYY-MM-DD') as ptr_end, ptr_apply_name, ptr_tel
		  ptr_name, ptr_cat_code, ptr_cat
		  from (select p.ptr_apply_code, p.ptr_code, p.mem_code, p.ptr_apply_date, p.ptr_start, p.ptr_end, p.ptr_apply_name, p.ptr_tel,
		  		pt.ptr_name, cat.ptr_cat_code, cat.ptr_cat from partner_apply p, partner pt, partner_cat cat
		  		where p.ptr_code = pt.ptr_code and pt.ptr_cat_code = cat.ptr_cat_code order by p.ptr_apply_code)
		  where mem_code=#{mem_code}

 select rownum as book_indexnum, book_code, mem_code, pub_code, book_start, num_of_per, to_char(book_date, 'YYYY-MM-DD') as book_date, pub_unit, pub_cat_code, pub_cat
		  from (select b.book_code, b.mem_code, b.book_start, b.num_of_per, b.book_date, po.pub_code, po.pub_unit, pc.pub_cat_code, pc.pub_cat
		  		from booking b, pub_office po, pub_cat pc where b.pub_code=po.pub_code and po.pub_cat_code=pc.pub_cat_code order by b.book_code)
		  where mem_code=#{mem_code}
